import { useParams } from "react-router-dom";
import { getBook } from "../data";

const Book = () => {
    const params = useParams();
    const book = getBook(parseInt(params.bookId))

    return ( 
        <main style={{padding: "1rem"}}>
            <h2>قیمت : {`${book.amount} تومان`}</h2>
            <p>{book.name}</p>
            <p>{book.due}</p>
        </main>
     );
}

export default Book;
