import { Link, NavLink, Outlet, useSearchParams } from "react-router-dom";
import { getBooks } from "../data";

const [search, setSearch] = useSearchParams();

const Books = () => {
    const books = getBooks();
    return ( 
        <>
            <div style={{ display: "flex"}}>
            <nav style={{borderLeft: "1px solid", padding: "1rem"}}> 
                <input type="text" placeholder="جستجوی کتاب" value={search.get("query") || ""} 
                onChange={e => setSearch(e.target.value)} />
                {books.filter((book, i) => {
                    let filter = search.get('filter');
                    let name = book.name;
                })}
                {books.map((book, index) => (
                    <NavLink style={({isActive}) => {
                        return {display: "block", margin: '1em', color: isActive? 'red' : ''}
                    }} to={`/books/${book.number}`} key={index} >
                        {book.name}
                    </NavLink>
                ))}
            </nav>
            <Outlet />
        </div>
        </>
     );
}

export default Books;
